import React from "react";
import { SidebarData } from "./SideBarData";
import "../css/Sidebar.css";
import { Typography } from "@mui/material";
import logo from '../logo/BangLogo.png';

const Sidebar: React.FC = () => {
    return (
        
      <div style={{ width: 250 }}>
        <img style={{ width: 80, height: 80, position: "relative", bottom: 30, top: 30, right: 80 }} src={logo} alt="Logo" />
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'Montserrat',
              fontStyle: 'bold',
              fontWeight: 1000,
              fontSize: 40,
              position: "relative",
              left: 100,
              bottom: 45,
              color: 'white',
              textDecoration: 'none',
            }}
          >
            Bang!
          </Typography>
        <ul className="sidebarRows">
          {SidebarData.map((val, key) => {
            return (
              <li
                key={key}
                className="row"
                id={val.path === window.location.pathname ? "active" : ""}
                onClick={() => (window.location.pathname = val.path)}
              >
                <div id="iconContainer">{val.icon}</div>
  
                <div id="titleContainer">
                  <h3>{val.title}</h3>
                </div>
              </li>
            );
          })}
        </ul>
        <hr />
      </div>
    );
  };

  export default Sidebar;
